function loadPokemon(e){var t=document.getElementById(e),l=JSON.parse(t.dataset.pkmn);t.querySelectorAll(".pkmn_name")[0].textContent=l.name,t.querySelectorAll(".pkmn_lv")[0].textContent=l.level,t.querySelectorAll(".pkmn_img_icon")[0].src="img/gender"+l.gender+".svg",t.querySelectorAll(".pkmn_item")[0].textContent=l.item,t.querySelectorAll(".pkmn-item-img")[0].src="https://nouthuca.com/img/item/"+l.item_img+".png?resize=82%2C74",t.querySelectorAll(".pkmn_ability")[0].textContent=l.ability;var r=validateType(l.type1);if(t.querySelectorAll(".ty_1")[0].textContent=r[0],t.querySelectorAll(".ty_1")[0].className+=" "+r[1],"none"!==l.type2){var o=validateType(l.type2);t.querySelectorAll(".ty_2")[0].textContent=o[0],t.querySelectorAll(".ty_2")[0].className+=" "+o[1]}else{var c=document.getElementById("ty_2");c.parentNode.removeChild(c)}t.querySelectorAll(".pkm-img")[0].src="https://nouthuca.com/img/pgl/"+l.poke_img+".png",t.querySelectorAll(".move_one")[0].textContent=l.skill1,t.querySelectorAll(".move_one")[0].className+=" bt_"+l.skill1_c,t.querySelectorAll(".move_two")[0].textContent=l.skill2,t.querySelectorAll(".move_two")[0].className+=" bt_"+l.skill2_c,t.querySelectorAll(".move_three")[0].textContent=l.skill3,t.querySelectorAll(".move_three")[0].className+=" bt_"+l.skill3_c,t.querySelectorAll(".move_four")[0].textContent=l.skill4,t.querySelectorAll(".move_four")[0].className+=" bt_"+l.skill4_c,t.querySelectorAll(".base_ps")[0].textContent=l.hp,t.querySelectorAll(".base_att")[0].textContent=l.att,t.querySelectorAll(".base_def")[0].textContent=l.def,t.querySelectorAll(".base_spa")[0].textContent=l.spa,t.querySelectorAll(".base_spd")[0].textContent=l.spd,t.querySelectorAll(".base_vel")[0].textContent=l.vel,t.querySelectorAll(".ev_hp")[0].textContent=l.ev_hp,t.querySelectorAll(".ev_att")[0].textContent=l.ev_att,t.querySelectorAll(".ev_def")[0].textContent=l.ev_def,t.querySelectorAll(".ev_spa")[0].textContent=l.ev_spa,t.querySelectorAll(".ev_spd")[0].textContent=l.ev_spd,t.querySelectorAll(".ev_vel")[0].textContent=l.ev_vel,t.querySelectorAll("."+l.stat_buff)[0].className+=" buff-color",t.querySelectorAll("."+l.stat_debuff)[0].className+=" debuff-color"}function validateType(e){switch(e){case"normal":return["Normal","c_type_normal"];case"fire":return["Fuego","c_type_fire"];case"water":return["Agua","c_type_water"];case"electric":return["Elect.","c_type_electric"];case"grass":return["Planta","c_type_grass"];case"ice":return["Hielo","c_type_ice"];case"fighting":return["Lucha","c_type_fighting"];case"poison":return["Veneno","c_type_poison"];case"ground":return["Tierra","c_type_ground"];case"flying":return["Volad.","c_type_flying"];case"psychic":return["Psiqu.","c_type_psychic"];case"bug":return["Bicho","c_type_bug"];case"rock":return["Roca","c_type_rock"];case"gost":return["Fant.","c_type_gost"];case"dragon":return["Dragon","c_type_dragon"];case"dark":return["Sinies.","c_type_dark"];case"steel":return["Acero","c_type_steel"];case"fairy":return["Hada","c_type_fairy"];default:return["Error","c_type_bug"]}}loadPokemon("pkmn_slot_01");